package main

import "testing"

func Test_isValid(t *testing.T) {
	type testData struct {
		input string
		want  string
	}
	tests := []testData{
		{
			input: "ABAC",
			want:  "YES",
		},
		{
			input: "abcdefghhgfedecba",
			want:  "YES",
		},
		{
			input: "aabbccddeefghi",
			want:  "NO",
		},
		{
			input: "aabbc",
			want:  "YES",
		},
		{
			input: "",
			want:  "YES",
		},
	}

	for _, test := range tests {
		got := isValid(test.input)
		if got != test.want {
			t.Errorf("test failed for input %s, got %s want %s", test.input, got, test.want)
		}
	}
}
